mapper.select:
select count(*)
from board
where userkey = #{userkey}

  
		  
		 select * 
		 from (board b, sale s) 
		 where (s.boardkey = b.boardkey)
		
		
		
  		    
  		order by salekey desc limit #{pageStart}, #{perPageNum}

  
		  
		 select * 
		 from (board b, buy bb) 
		 where (b.boardkey = bb.boardkey)
		
		
		
  		    
  		order by buykey desc limit #{pageStart}, #{perPageNum}

select userkey, id, name,
nickname,
point, cheater from user where userkey=#{userkey}

  
select count(boardkey)
from sale
where boardkey = #{boardkey}

  
select count(boardkey)
from buy
where boardkey = #{boardkey}

mapper.update:
update board
group1 = #{group1}, group2 = #{group2}, title = #{title}, text = #{text}
where boardkey = #{boardkey}
