mapper.select:
select filekey, fname, oname, fileid, filetype, filesize, filepath
from file
where boardkey=#{boardkey}

mapper.update:
update file
fname = #{fname}, oname = #{oname}, filetype = #{filetype}, filesize = #{filesize}, filepath = #{filepath}, fileData = #{fileData}
where boardkey=#{boardkey} and fileid=#{fileid}

mapper.select:
select max(fileid)
from file
where boardkey=#{boardkey}
